mapper.select:
SELECT *
FROM section_projects
WHERE section_id = #{sectionId}
ORDER BY `order`;

SELECT *
FROM section_projects
WHERE section_id = #{sectionId} AND url = #{url};

SELECT *
FROM projects
ORDER BY order_number;

SELECT ${columns}
FROM projects
WHERE !project_hidden
ORDER BY order_number;

SELECT *
FROM projects
WHERE parent_id = #{parentId}
ORDER BY order_number;
